import React from 'react';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            f1Complete: false,
            f2Complete: false,
            f3Complete: false,
            name: null,
            email: null,
            password: null
        }
    }

    onFormSubmit = (state) => {
        console.log(state);
    }
    checkCurrentState = () => {
        console.log(this.state);
    }

    handleInputChange = (event) => {
        var stateId = event.target.id;
        var stateValue = event.target.value;
        this.setState({
            [stateId]: stateValue
        });
    }

    onbuttonClick = (event) => {
        var step = event.target.id
        event.preventDefault();
        this.setState({
            [step]: true
        });
        this.onFormSubmit(this.state);
    }

    render() {
        var f1Complete = (this.state.f1Complete && !this.state.form2Complete);
        var f2Complete = (this.state.f2Complete && this.state.form1Complete);
        return (
            <div>
                <h1>Hello world</h1>
                {f1Complete ? 
                 <>    
                 <div>
                     <form id='f2Complete'>
                 <label>
                     Address:
                 <br />
                     <textarea id='name' rows='1' cols='20' onChange={this.handleInputChange} />
                 </label>
                 <br />
                 <br />
                 <label>
                     Email:
                 <br />
                     <textarea id='email' rows='1' cols='20' onChange={this.handleInputChange} />
                 </label>
                 <br />
                 <br />
                 <label>
                     Password:
                 <br />
                     <textarea id='password' rows='1' cols='20' onChange={this.handleInputChange} />
                 </label>
                 <br />
                 <br />
                 <button onClick={this.onbuttonClick} id='f2Complete'>SUBMIT</button>
             </form>
                 </div>
                 <button onClick={this.checkCurrentState}>Check State</button>
             </>
                 :
                <>    
                <div>
                    <form id='f1Complete'>
                <label>
                    Name:
                <br />
                    <textarea id='name' rows='1' cols='20' onChange={this.handleInputChange} />
                </label>
                <br />
                <br />
                <label>
                    Email:
                <br />
                    <textarea id='email' rows='1' cols='20' onChange={this.handleInputChange} />
                </label>
                <br />
                <br />
                <label>
                    Password:
                <br />
                    <textarea id='password' rows='1' cols='20' onChange={this.handleInputChange} />
                </label>
                <br />
                <br />
                <button onClick={this.onbuttonClick} id='f1Complete'>SUBMIT</button>
            </form>
                </div>
                <button onClick={this.checkCurrentState}>Check State</button>
            </>
            }
            </div>
        );
    }
}



export default App;
