import React from 'react';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: null,
            email: null,
            password: null
        }
    }

    onFormSubmit = (state) => {
        console.log(state);
    }
    checkCurrentState = () => {
        console.log(this.state);
    }

    handleInputChange = (event) => {
        var stateId = event.target.id;
        var stateValue = event.target.value;
        this.setState({
            [stateId]: stateValue
        });
    }

    onbuttonClick = (event) => {
        event.preventDefault();
        console.log(this.state);
        this.onFormSubmit(this.state);
    }

    render() {

        return (
            <div>
                <h1>Hello world</h1>
                <div>
                    <form>
                <label>
                    Name:
                <br />
                    <textarea id='name' rows='1' cols='20' onChange={this.handleInputChange} />
                </label>
                <br />
                <br />
                <label>
                    Email:
                <br />
                    <textarea id='email' rows='1' cols='20' onChange={this.handleInputChange} />
                </label>
                <br />
                <br />
                <label>
                    Password:
                <br />
                    <textarea id='password' rows='1' cols='20' onChange={this.handleInputChange} />
                </label>
                <br />
                <br />
                <button onClick={this.onbuttonClick}>SUBMIT</button>
            </form>
                </div>
                <button onClick={this.checkCurrentState}>Check State</button>
            </div>
        );
    }
}



export default App;
